<template>  <div class="time">    <div class="time-wrapper" ><!--      <div class="time-title-wrapper">--><!--        <span class="time-title-text">tudo番茄时钟</span>--><!--      </div>-->        <div class="time-title-todo-wrapper">            <span class="time-title-text">正在{{todo}}</span>        </div>      <div class="time-content-wrapper">        <span class="time-content">{{ minute }}:{{ second }}</span>      </div>      <div class="time-button">        <div class=" title-button-start" @click="onStart" >            <span class="icon-start"></span>            <span class="title-button-text">开始</span>        </div>          <div class="title-button-pause" @click="onPause">              <span class="icon-stop2"></span>              <span class="title-button-text">暂停</span>          </div>          <div class="title-button-reset" @click="onReset">              <span class="icon-stop1"></span>              <span class="title-button-text">重置</span>          </div>      </div>    </div>  </div></template><script>export default {    name: "Time",    data() {        return {            min:25,            sec: 0,            totalsec:59,            todo:'工作',            work:true,            isdisable:false        }    },    methods: {        num(n) {            return n < 10 ? "0" + n : "" + n//让一位数变为两位数        },        onStart(){            if(this.isdisable){                return            }            this.isdisable = true            console.log('点击一次');             this.time = setInterval(()=>{                if(this.second==='00' && this.minute !== '00' ){                    this.sec = this.totalsec                    this.min-=1                }else if(this.second==='00' && this.minute === '00'){                    clearInterval(this.time)                    this.work = !this.work                    this.min =5                    this.sec =0                    this.work? this.todo ='工作' : this.todo ='休息'                }else {                    this.sec-=1                }            },1000)        },        onPause(){            clearInterval(this.time)        },        onReset(){            clearInterval(this.time)            if(this.work){                this.min =25                this.sec =0            }else{                this.min =5                this.sec =0            }        },    },    computed: {        second() {            return this.num(this.sec)//让一位数变为两位数        },        minute() {            return this.num(this.min)//让一位数变为两位数        }    },}</script><style lang="scss" scoped>@import "../assets/styles/global";    .time{        position: absolute;        width: 100%;        height: 100%;        bottom:px2rem(100);        top:0;        background-image: url("../../public/img/bg.jpg");        background-size:cover ;        @include center;        .time-wrapper{            color: white;            .time-title-wrapper{                padding-bottom: px2rem(100);                .time-title-text{                    font-size: px2rem(60);                    color:rgb(188,38,26);                }            }            .time-title-todo-wrapper{                @include center;                font-size: px2rem(70);                padding-bottom: px2rem(20);                .time-title-text{                }            }            .time-content-wrapper{                padding-bottom: px2rem(100);                .time-content{                    font-weight: bold;                    font-size: px2rem(280);                }            }            .time-button{                display: flex;                font-size: px2rem(45);                padding-left: px2rem(70);                @include center;                .title-button-start{                    flex: 1;                }                .title-button-pause{                    flex: 1;                }                .title-button-reset{                    flex: 1;                }            }        }    }</style>