<template>    <div class="time">        <div class="time-wrapper">            <!--      <div class="time-title-wrapper">-->            <!--        <span class="time-title-text">tudo番茄时钟</span>-->            <!--      </div>-->            <div class="time-title-todo-wrapper">                <span class="time-title-text">正在{{todo}}</span>            </div>            <div class="time-content-wrapper">                <span class="time-content">{{ min }}:{{ sec }}</span>            </div>            <div class="time-button">                <div class=" title-button-start" @click="handleBtn">                    <span :class="!isWork ? 'icon-start' : 'icon-stop2'"></span>                    <span class="title-button-text">{{btnText}}</span>                </div>                <!--                <div class="title-button-pause" @click="onPause">-->                <!--                    <span class="icon-stop2"></span>-->                <!--                    <span class="title-button-text">暂停</span>-->                <!--                </div>-->                <div class="title-button-reset" @click="onReset">                    <span class="icon-stop1"></span>                    <span class="title-button-text">重置</span>                </div>            </div>        </div>    </div></template><script>    const WORKTIME = 25 * 60    const RELAXTIME = 5 * 60    export default {        name: "Time",        data() {            return {                isWorkTime: true,                time: WORKTIME,                isWork: false,                isDisable: false            }        },        computed: {            min() {                return Math.floor(this.time / 60).toString().padStart(2, '0')            },            sec () {                return (this.time - this.min * 60).toString().padStart(2, '0')            },            btnText() {                return this.isWork ? '暂停' : '开始'            },            todo () {                return this.isWorkTime ? '工作' : '休息'            }        },        methods: {            handleBtn() {                this.isWork ? this.onPause() : this.onStart()            },            onStart() {                this.isWork = true                this.timer = setInterval(() => {                    this.time--                    if (this.time === 0) {                        clearInterval(this.timer)                        this.isWork = false                        this.isWorkTime = !this.isWorkTime                        this.time = this.isWorkTime ? WORKTIME : RELAXTIME                    }                }, 1000)            },            onPause() {                this.isWork = false                clearInterval(this.timer)            },            onReset() {                clearInterval(this.timer)                this.isWork = false                this.time = this.isWorkTime ? WORKTIME : RELAXTIME            }        }    }</script><style lang="scss" scoped>    @import "../assets/styles/global";    .time {        position: absolute;        width: 100%;        height: 100%;        bottom: px2rem(100);        top: 0;        background-image: url("../../public/img/bg.jpg");        background-size: cover;        @include center;        .time-wrapper {            color: white;            .time-title-wrapper {                padding-bottom: px2rem(100);                .time-title-text {                    font-size: px2rem(60);                    color: rgb(188, 38, 26);                }            }            .time-title-todo-wrapper {                @include center;                font-size: px2rem(70);                padding-bottom: px2rem(20);                .time-title-text {                }            }            .time-content-wrapper {                padding-bottom: px2rem(100);                .time-content {                    font-weight: bold;                    font-size: px2rem(280);                }            }            .time-button {                display: flex;                font-size: px2rem(45);                padding-left: px2rem(70);                @include center;                .title-button-start, .title-button-pause, .title-button-reset {                    flex: 1;                    text-align: center;                    .icon-start, .icon-stop2, .icon-stop1 {                        margin-right: px2rem(6);                        position: relative;                        top: px2rem(4);                    }                }            }        }    }</style>