<template>    <div class="time-title">        <div class="time-title-wrapper" >            <span class="time-title-task-tiele">任务：</span>            <span class="time-title-task-text">{{task}}</span>        </div>        <div class="time-title-edit-task" @click="createTask">           <i class="icon-edit"></i>        </div>    </div></template><script>    import Bus from "../utils/evenBus";    export default {        name: "TimeTitle",        data(){            return{                task: '暂无任务',                popupVisible:false            }        },        methods:{            createTask(){                Bus.$emit('setpopupvisible' ,this.popupVisible= true)            },        },        created() {            Bus.$on('settask',(newtask)=>{                this.task = newtask            })        }    }</script><style lang="scss" scoped>    @import "../assets/styles/global";     .time-title{         position:fixed;         background: white;         z-index: 200;         left: 0;         top: 0;         display: flex;         width: 100%;         box-sizing: border-box;         box-shadow: px2rem(0) px2rem(0) px2rem(10) px2rem(0) rgba(0,0,0,0.4);         .time-title-wrapper{            flex: 10;             left: 0;             padding: px2rem(30);             font-size: px2rem(36);             .time-title-task-title{                 font-weight: bold;             }             .time-title-task-text{                 color: #999;             }         }         .time-title-edit-task{             flex: 1;             right: 0;             padding: px2rem(30);             .icon-edit{                 font-size: px2rem(44);                 @include center;             }         }     }</style>