<template>    <div class="time-popup" v-show="popupvisible">        <div class="time-popup-wrapper">            <div class="time-popup-title">                <span class="time-popup-title-text">创建任务</span>            </div>            <div class="time-popup-text">                <textarea type="text" class="time-popup-text-input"  placeholder="点此输入任务"                width="100%"                v-model="task"                autocomplete="on"                maxlength="30"                rows="4"                >                </textarea>            </div>            <div class="time-popup-confirm-and-close">                 <div class="confirm-wrapper">                     <span class="confirm" @click="createNewtask">确认</span>                 </div>                <div class="close-wrapper" @click="closepopup">                    <span class="close">关闭</span>                </div>            </div>        </div>    </div></template><script>    import Bus from '../../utils/evenBus'    export default {        name: "popup",        data(){            return{                task:'',                popupvisible:false            }        },        methods:{            closepopup(){                this.popupvisible = false            },            createNewtask(){               Bus.$emit('settask',this.task)                this.popupvisible = false            }        },        created() {            Bus.$on('setpopupvisible',(newpopupvisible)=>            this.popupvisible = newpopupvisible            )        }    }</script><style lang="scss" scoped>    @import "../../assets/styles/global";.time-popup{    position: absolute;    width:100%;    height: 100%;    z-index: 2000;    @include center;    flex-direction: column;    font-size: px2rem(50);    background-color: rgba(255,255,255,0.6);    .time-popup-wrapper{        width: px2rem(700);        height: px2rem(400);        background-color: white;        border-radius: px2rem(20);        overflow: hidden;       position: relative;        .time-popup-title{            padding: px2rem(30);            .time-popup-title-text{                @include center;                color: black;                font-weight: bold;            }        }        .time-popup-text {            padding: px2rem(20);            .time-popup-text-input{                width: 100%;                border:none;                font-size: px2rem(36);                &:focus{                    border: none;                }            }        }        .time-popup-confirm-and-close{            position: absolute;            bottom: 0;            background-color: deepskyblue;            width: 100%;            color: white;            display: flex;            .confirm-wrapper{                padding: px2rem(15);                flex: 1;                @include center;                box-sizing: border-box;                border-right: px2rem(2) solid white;            }            .close-wrapper{                flex: 1;                @include center;                padding: px2rem(15);            }        }    }}</style>